import { Alchemy, Network } from 'alchemy-sdk';
import { useEffect, useState } from 'react';

import './App.css';
// import { ListItem, ListItemText } from '@mui/material';
// import List from '@mui/material/List';

// Refer to the README doc for more information about using API
// keys in client-side code. You should never do this in production
// level code.
const settings = {
  apiKey: process.env.REACT_APP_ALCHEMY_API_KEY,
  network: Network.ETH_MAINNET,
};


// In this week's lessons we used ethers.js. Here we are using the
// Alchemy SDK is an umbrella library with several different packages.
//
// You can read more about the packages here:
//   https://docs.alchemy.com/reference/alchemy-sdk-api-surface-overview#api-surface
const alchemy = new Alchemy(settings);

function App() {
  const [blockNumber, setBlockNumber] = useState();
  const [blockDetails, setBlockDetails] = useState();
  const [blockTransactions, setBlockTransactions] = useState([]);

  useEffect(() => {

    async function getBlockNumber() {
      setBlockNumber(await alchemy.core.getBlockNumber());
    }
    getBlockNumber();

  }, []);

  useEffect(() => {
    async function getBlockDetails(){
      try{
        let listTransactions = [];
        const data = await alchemy.core.getBlock(blockNumber)
        data["transactions"].forEach((transaction, index)=>{
          listTransactions.push(transaction['hash']);
        });
        setBlockDetails(data);
        setBlockTransactions(listTransactions);
      }catch(error){
        setBlockDetails([]);
      }
    }

    getBlockDetails();
  }, [blockNumber, setBlockDetails, setBlockTransactions]);

  return(
  <div className='App'>
    <div className="BlockDetails">
      <div className='item'>Block Number: {blockNumber}</div>
      <div className='item'>Time stamp: {!blockDetails ? 'Loading' : blockDetails['timestamp']}</div> 
      <div className='item'>Miner: {!blockDetails ? 'Loading' : blockDetails['miner']}</div>
    </div>
    <ul className='TransactionsList'>
      {
      blockTransactions.map((value)=> <li>{value}</li>)
      }
    </ul>
  </div>
  );
}

export default App;
